function ativarVerMais() {
  const cards = document.querySelectorAll(".card");

  cards.forEach(card => {
    const descricaoContainer = card.querySelector(".descricao-container");
    if (!descricaoContainer) return;

    const textoCurto = descricaoContainer.querySelector(".descricao-texto");
    const textoCompleto = descricaoContainer.querySelector(".descricao-completa");

    if (!textoCompleto || !textoCurto) return;

    // Remove botão antigo se existir
    const btnExistente = descricaoContainer.querySelector(".ver-mais");
    if (btnExistente) btnExistente.remove();

    // Para medir o elemento escondido, remove temporariamente 'hidden' e 'position:absolute' se houver, mas com visibilidade 'hidden' e altura 'auto' para não impactar layout
    textoCompleto.style.display = "block";
    textoCompleto.style.position = "absolute";
    textoCompleto.style.visibility = "hidden";
    textoCompleto.style.height = "auto";

    const alturaCurto = textoCurto.getBoundingClientRect().height;
    const alturaCompleto = textoCompleto.getBoundingClientRect().height;

    // Voltar o textoCompleto ao estado inicial (escondido)
    textoCompleto.style.display = "";
    textoCompleto.style.position = "";
    textoCompleto.style.visibility = "";
    textoCompleto.style.height = "";

    if (alturaCurto < alturaCompleto) {
      const botaoVerMais = document.createElement("button");
      botaoVerMais.className = "ver-mais text-[#4F46E5] text-sm font-semibold mt-1 cursor-pointer select-none bg-transparent border-none p-0";
      botaoVerMais.textContent = "Ver mais";

      descricaoContainer.appendChild(botaoVerMais);

      botaoVerMais.addEventListener("click", () => {
        if (textoCompleto.classList.contains("hidden")) {
          textoCompleto.classList.remove("hidden");
          textoCurto.classList.add("hidden");
          botaoVerMais.textContent = "Ver menos";
          descricaoContainer.style.maxHeight = "none";
        } else {
          textoCompleto.classList.add("hidden");
          textoCurto.classList.remove("hidden");
          botaoVerMais.textContent = "Ver mais";
          descricaoContainer.style.maxHeight = null;
        }
      });
    } else {
      descricaoContainer.style.maxHeight = null;
    }
  });
}
